});

function callback(err, autoscalers, nextQuery, apiResponse) {
  if (nextQuery) {
    // More results exist.
    zone.getAutoscalers(nextQuery, callback);
  }
}

zone.getAutoscalers({
  autoPaginate: false
}, callback);

zone.getAutoscalers().then(function(data) {
  var autoscalers = data[0];
