Job.prototype.getQueryResults = function(options, callback) {
  if (util.is(options, 'function')) {
    callback = options;
    options = {};
  }

  options = options || {};
  options.job = this;

  return this.bigQuery.query(options, callback);
};

module.exports = Job;
