DatastoreRequest.prototype.delete = function(keys, callback) {
  callback = callback || util.noop;

  var req = {
    mutation: {
      delete: util.arrayize(keys).map(entity.keyToKeyProto)
    }
  };

  if (this.id) {
    this.requests_.push(req);
    return;
  }

  this.makeReq_('commit', req, callback);
};

