Zone.prototype.export = function(localPath, callback) {
  this.getRecords(function(err, records) {
    if (err) {
      callback(err);
      return;
    }

    var stringRecords = records.map(util.exec('toString')).join('\n');

    fs.writeFile(localPath, stringRecords, 'utf-8', function(err) {
      callback(err || null);
    });
  });
};

