Zone.prototype.replaceRecords = function(recordType, newRecords, callback) {
  var self = this;

  this.getRecords(recordType, function(err, recordsToDelete) {
    if (err) {
      callback(err);
      return;
    }

    self.createChange({
      add: newRecords,
      delete: recordsToDelete
    }, callback);
  });
};

