Zone.prototype.deleteRecordsByType_ = function(recordTypes, callback) {
  var self = this;

  this.getRecords(recordTypes, function(err, records) {
    if (err) {
      callback(err);
      return;
    }

    if (records.length === 0) {
      callback();
      return;
    }

    self.deleteRecords(records, callback);
  });
};
