function PubSub(options) {
  options = options || {};

  this.connection = new conn.Connection({
    credentials: options.credentials,
    keyFilename: options.keyFilename,
    scopes: SCOPES
  });
  this.projectId = options.projectId;
  this.projectName = '/projects/' + this.projectId;
}

